mapper.select:
SELECT cart_id, user_id, book_id, quantity, cart_date
FROM cart
WHERE user_id = #{user_id}
AND book_id = #{book_id}

mapper.update:
UPDATE cart
SET quantity = #{quantity}
WHERE cart_id = #{cart_id}

mapper.delete:
DELETE FROM cart
WHERE user_id = #{user_id}
AND book_id IN

    #{bookId}

